class Solution {
public:
    int maxOperations(string s) {

             long long n = s.size() , ans = 0  , cnt = 0 ;


             for(int i =  0 ; i  <  n  ; i ++){

                     if(s[i] == '1'){
                               cnt ++ ;
                             if(i + 1 < n &&  s[i + 1] == '0'){
                                       ans += cnt ;
                             }
                            //   cnt ++ ;

                     }
             }
            return ans;
    }
};
