class Solution {
public:
    int maxOperations(string s) {
        int one=0, ans=0;

        if(s[0]=='1') one++;

        for(int i=1; i<s.size(); i++){
            if(s[i]=='1'){

                if(s[i-1]=='0') ans+=one;
                one++;
            }
            // cout << ans << " ";
        }

        if(s.back()=='0') ans+=one;

        return ans;
    }
};
