class Solution {
public:
    int maxOperations(string s) {
        int x = 0;
        int ans = 0;
        int prev = 0;
        for(char c: s){
            if(c == '0') {
                if (prev) ans += x;
                prev = 0;
            }
            else {
                x++;
                prev = 1;
            }
        }
        return ans;
    }
};
