class Solution {
    public int maxOperations(String s) {
        int res = 0, beg = 0, cnt = 0;
        while (beg < s.length()) {
            if (s.charAt(beg) == '1') {
                if (beg > 0 && s.charAt(beg - 1) == '0')
                    res += cnt;
                cnt++;
            }
            else if (beg == s.length() - 1)
                res += cnt;
            beg++;
        }
        return res;
    }
}
