class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();

        int i = 0;

        int one = 0;
        int ans = 0;

        while (i < n)
        {
            if (s[i] == '1')
            {
                while (i < n && s[i] == '1')
                {
                    one++;
                    i++;
                }

                int zero = 0;
                while (i < n && s[i] == '0')
                {
                    i++;
                    zero++;
                }

                if (zero > 0)
                {
                    ans += one;
                }
            }
            else
            {
                i++;
            }
        }

        return ans;
    }
};
