class Solution {
public:
    int maxOperations(string s) {
        vector<int> ones; ones.push_back(0);
        for (char c : s) {
            if (c == '0') {
                if (ones.back() > 0) ones.push_back(0);
            } else ones[ones.size() - 1]++;
        }
        reverse(ones.begin(), ones.end());
         int sum = 0;
        for (int i = 0; i < ones.size(); i++) {
            sum += ones[i] * i;
        }
        return sum;

    }
};
