class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int cnt = 0;
        int n = s.length();
        while (n && s[n - 1] == '1') {
            n--;
        }
        int f = 1;
        for (int i = n - 2; i >= 0; i--) {
            if (s[i] == '1') {
                if (s[i + 1] == '0')cnt++;
                if (f) {
                    int j = i;
                    while (j >= 0 && s[j] == '1') {
                        ans += cnt;
                        j--;
                    }
                    i = j + 1;
                }   
            }
            else {
                f = 1;
            }
        }
        return ans;
    }
};
