class Solution {
    public int maxOperations(String s) {
        int count = 0;
        int oneCount = 0;
        int prev = -1;

        for (int i = 0; i <= s.length(); ++i) {
            int digit = i == s.length()? 1: (s.charAt(i) - '0');
            if (digit == 1) {
                if (prev == 0) {
                    count += oneCount;
                }
                oneCount++;
            } 
            prev = digit;
        }
        return count;
    }
}
