class Solution {
public:
    int maxOperations(string s) {
        int zero=0;
        s.push_back('1');
        int res=0;
        for(int i=s.length()-2;i>=0;i--)
        {
            if(s[i]=='0' && s[i+1]=='1') zero++;
            if(s[i]=='1') res+=zero;

        }
        return res;

    }
};
