class Solution {
public:
    int maxOperations(string s) {
        int count=0,idx=0;
        int n=s.length()-1;
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                idx++;
            }
            if(s[i]=='1' && s[i+1]=='0'){ 
                count+=idx;
            }
           // cout<<i<<" ";

        }
       // cout<<endl;

        return count;
    }
};
