class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        int n = s.size();
        bool suc = true;
        int ans = 0;
        for(auto i = 0; i < n; ++i){
            if(s[i] == '0'){
                if(suc){
                    ans += cnt;
                    suc = false;
                }
            }else{
                cnt++;
                suc = true;
            }
        }
        return ans;
    }
};
