impl Solution {
    pub fn max_operations(s: String) -> i32 {
        let mut ans = 0;
        let mut p = 0;
        let mut k = false;
        let mut h = Vec::new();

        for c in s.chars() {
            if c == '1' {
                h.push(1);
                k = true;
            }
            if c == '0' && k {
                h.push(0);
                k = false;
            }
        }

        for &x in &h {
            if x == 1 {
                p += 1;
            } else {
                ans += p;
            }
        }

        ans
    }
}
