class Solution {
public:
    int maxOperations(string s) {
        const int n = s.length();
        int pre = 0;
        int total = 0;
        for(int i = 0; i < n; i ++ ) {
            if(s[i] == '1') {
                if(i > 0 && s[i-1] == '0') {
                    total += pre;
                }
                pre += 1;
            }
            else{
                continue;
            }
        }
        if(s[n-1] == '0') {
            total += pre;
        }
        return total;
    }
};
