class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int ones = 0;
        bool add = false;

        for(auto& a:s)
        {
            if(a == '1') 
            {
                if(add) ans += ones;
                ones++;
                add = false;
            }
            else add = true;
        }

        if(add) ans += ones;
        return ans;
    }
};
