class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int one = 0;
        string tmp;
        for(int i = 0;i <s.size();++i){
            if(i != 0 && s[i] == '0' &&s[i-1] == '0')continue;
            tmp+= s[i];
        }
        s = tmp;
       // cout <<s <<'\n';
        for(int i = 0;i <s.size();++i){
            if(s[i] == '1')one++;
            else ans += one;
        }
        return ans;
    }
};
