class Solution {
public:
    int maxOperations(string s) {
        int answer = 0;
        int ones = 0;
        int zeroes = 0;
        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '1') {
                if (zeroes != 0) {
                    answer += ones;
                    zeroes = 0;
                }
                ones++;
            } else {
                zeroes++;
            }
        }
        if (zeroes != 0) {
            answer += ones;
            zeroes = 0;
        }
        return answer;
    }
};
