class Solution:
    def maxOperations(self, s: str) -> int:
        ans = cnt = i = 0
        n = len(s)
        while i < n:
            while i < n and s[i] == '0':
                i += 1
            ans += cnt
            while i < n and s[i] == '1':
                cnt += 1
                i += 1
        return ans
