#include<bits/stdc++.h>

class Solution {
public:
    int maxOperations(string s) 
    {
        vector<int> vec;
        int ct=0;
        for(int i=0;i<10;i++) vec.push_back(ct++);

        int count=0, ans=0;

        int n=s.size();
        if(s[n-1]=='0') count=1;

        for(int i=n-1;i>=0;i--)
        {
            if(s[i]=='0') ;

            else 
            {
                while(i>=0 && s[i]=='1')
                {
                    ans+= count;
                    i--;
                }
                count+=1;
            }
        }
        return ans;
    }
};
