class Solution {
public:
    int maxOperations(string s) {
     vector<char>a;
      int n=s.size();
        for(int i=0;i<n;i++)
        {
            if(s[i]=='1')
            {
                a.push_back('1');
            }
            else
            {
                int j=i;
                while(j<n && s[i]==s[j])
                {
                    j++;
                }
                a.push_back('0');
                i=j-1;
            }
        }
        int cnt=0;
        int ans=0;
        for(auto it:a)
        {
            if(it=='1')
            {
                cnt++;
            }
            else
            {
                ans+=cnt;
            }
        }
        return ans;
    }
};
