class Solution {
public:
    int maxOperations(string s) {
        int canMove = s.back() == '0';
        vector<int> t(s.size());
        for (int i = s.size() - 1; i >= 0; i--) {
            if (s[i] == '1' && canMove) t[i] = 1;
            if (s[i] == '1') canMove = 0;
            else canMove = 1;
        }
        int cnt = 0, ans = 0;
        for (int i = 0; i < s.size() - 1; i++) {
            if (s[i] == '1') cnt++;
            if (s[i] == '1' && t[i] == 1) {
                ans += cnt;
            }
        }
        return ans;
    }
};
