class Solution {
public:
    int maxOperations(string s) {
        //from right count number of groups of 0s
        //for each 1 add that 


        int zero_groups = 0;
        char last = 'x';
        int total = 0;

        for(int i = s.size() -1; i >= 0; i--){
            if(s[i] != '0'){
                if(last == '0'){
                    zero_groups++;
                }
                total += zero_groups;
            }
            last = s[i];
        }
        return total;
    }
};
