class Solution {
public:
    int maxOperations(string s) {
        int now = 0;
        int res = 0;
        if(s[0] == '1')now ++;
        for(int i = 1; i < s.size(); i ++)
        {
            if(s[i] == '1') now ++;
            else if(s[i - 1] == '1')res += now;
        }
        return res;
    }
};
