class Solution {
public:
    int maxOperations(string s) {
        int count = 0, ans = 0, i, j;

        for (i = 0; i < s.size(); i++) {
            if (s[i] == '1') count++;
            else {
                for (j = i + 1; j < s.size(); j++) {
                    if (s[j] == '1') break;
                }
                ans += count;
                i = j - 1;
            }
        }

        return ans;
    }
};
