class Solution:
    def maxOperations(self, s: str) -> int:
        ans = t = res = 0
        sw = 0
        for a, b in pairwise(s):
            # print(a,b)
            if a == "1":
                t += 1
            if a == "1" and b == "0":
                res += t
            if a == "0" and b == "1":
                ans += res
                res = 0
            # print(t, res , ans)
        if s[-1] != "1":
            ans += res
        return ans
