class Solution {
public:
    int maxOperations(string s) {
        int ans=0,val=0,flag=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                flag=1;
                val++;
            }else if(flag==1){
                flag=0;
                ans+=val;
            }
        }
        return ans;
    }
};
