class Solution:
    def maxOperations(self, s: str) -> int:
        sz, ans = 0, 0
        if (s[0] == '1'): sz = 1
        for i in range(1, len(s)):
            if s[i] == '0':
                if s[i-1] == '0': continue
                ans += sz
            else:
                sz+=1
        return ans
