class Solution {
public:
    int maxOperations(string s) {
        const int N = s.size();
        int ans = 0;
        bool zero = false;
        for (int i = 0, c = 0; i <= N; ++i) {
            if (i == N or s[i] == '1') {
                if (zero) ans += c;
                ++c;
                zero = false;
            }
            if (i < N and s[i] == '0') zero = true;
        }
        return ans;
    }
};
