class Solution:
    def maxOperations(self, s: str) -> int:

        #s = "1011010"

        count = 0
        one = 0
        x = False

        for i in range(len(s)):
            if s[i] == "1":
                one += 1
                x = True
            elif one >= 0 and x:
                count += one
                x = False

        return count
