class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int i = 0;
        int n = s.size();
        int previous_count = 0;
        while (true) {
            while (i < n && s[i] != '1') {
                i += 1;
            }
            if (i == n) {
                break;
            }

            int one_end = i;
            while (one_end + 1 < n && s[one_end + 1] == '1') {
                one_end += 1;
            }
            if (one_end == n - 1) {
                break;
            }

            ans += previous_count;
            ans += (one_end - i + 1);
            previous_count += (one_end - i + 1);
            i = one_end + 1;
        }
        return ans;
    }
};
