class Solution {
public:
    int maxOperations(string s) {
        int oneCnt = 0;
        int ans = 0;
        for(int i = 0; i + 1 < s.length(); i++)
        {
            if (s[i] == '1' && s[i + 1] == '0')
            {
                ans += 1;
                ans += oneCnt;
            }
            if (s[i] == '1')
            {
                oneCnt++;
            }
        }
        return ans;
    }
};
