class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int sum=0,c=0;
        for(int i=n-1;i>0;i--){
            if(s[i]=='0'&&s[i-1]=='1'){
                c++;
            }
            else if(s[i]=='1'){
                sum+=c;
            }
        }
        if(s[0]=='1')
        sum+=c;
        return sum;
    }
};
