class Solution:
    def maxOperations(self, s: str) -> int:
        curr = 0 

        ans = 0 
        n = len(s)

        i = 0 
        while i < n:
            if s[i] == "1" :
                if i + 1 < n and s[i+1] == "0" :
                    ans = ans + 1 + curr 
                    curr += 1
                else :
                    curr += 1 
            i += 1 
        return ans
