class Solution:
    def maxOperations(self, s: str) -> int:
        a = []
        cur = 0
        for c in s:
            if c == '0':
                if cur > 0: a.append(cur)
                cur = 0
            else:
                cur += 1
        # print(a)
        res = 0
        cur = 0
        for x in a:
            cur += x
            res += cur
        return res
