class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), c1 = 0;
        int cnt = 0;
        for(int i = 0; i < n; ){
            if(s[i] == '1') c1++, i++;
            if(s[i] == '0') {
                while(s[i] == '0') i++;
                cnt += c1;
            }
        }

        return cnt;
    }
};
