class Solution {
public:
    int maxOperations(string s) {
        vector<int>pos;
        int n = s.size();
        for(int i = 0;i<n;i++){
            if(s[i] == '1'){
                pos.push_back(i);
            }
        }
        pos.push_back(n);
        int ret = 0;
        int m = pos.size();
        for(int i = 1;i<m;i++){
            if(pos[i] == pos[i-1]+1)    continue;
            ret += i;
        }
        return ret;
    }
};
