class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        long long ans = 0;
        long long count = 0;
        int i = 0;
        while(i<n)
        {
            while(i<n && s[i]=='0') ++i;
           // if(i==n) break;


            while(i<n && s[i]=='1')
            {
                count++;
                ++i;
            }
            if(i==n) break;


            ans+=count;
        }
        return ans;
    }
};
