class Solution {
public:
    int maxOperations(string s) {
        int oneCount = 0;
        int oper = 0;
        int n = s.length();

        for(int i=0; i<n; i++) {
            if(s[i] == '0' && (i+1 == n || (i + 1 < n && s[i+1] == '1'))) {
                oper += oneCount;
            }
            if(s[i] == '1') oneCount++;
        }

        return oper;
    }
};
