class Solution:
    def maxOperations(self, s: str) -> int:
        res = cur = 0
        n = len(s)
        i = n - 1
        while i >= 0:
            if s[i] == '0':
                cur += 1
                while i >= 0 and s[i] == '0':
                    i -= 1
            if i >= 0 and s[i] == '1':
                res += cur
                i -= 1
        return res
