class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int cou[100005];
        for(int i=0;i<100005;i++)cou[i] = 0;
        for(int i=(int)s.size()-1;i>=0;i--){
            if(i == (int)s.size()-1){
                if(s[i] == '0'){
                    cou[i] = 1;
                }
            }
            else{
                if(s[i] == '0' && s[i+1] == '1'){
                    cou[i] = cou[i+1]+1;
                }
                else{
                    cou[i] = cou[i+1];
                }
            }
        }
        for(int i=0;i<(int)s.size();i++){
            if(s[i] == '1'){
                ans += cou[i];
            }
        }
        return ans;
    }

};
