class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int last = 0;
        int cnt = 0;
        for (int i = 0; i < s.size(); i++){
            if (s[i] == '1'){
                cnt++;
                last = 1;
            }
            else if (s[i] == '0'){
                if (last == 1){
                    res += cnt;
                    last = 0;
                }
            }

        }
        return res;
    }
};
