class Solution {
public:
    int maxOperations(string s) {
        int ans=0,cnt=0,p=0;
        stack<pair<int,int>> q;

        for(int i=s.size()-1;i>=0;--i){
            if(s[i]=='1'){
                q.push({i,1});
                cnt++;
                if(i==s.size()-1) p=1;
            }
        }
        if(p) cnt=0;
        while(q.size()>1){
            auto a=q.top();
            q.pop();
            auto b=q.top();
            q.pop();
            if((a.first+1)<b.first){
                ans+=a.second;
            }
            q.push({b.first,a.second+b.second});
        }

        return ans+cnt;
    }
};
