class Solution:
    def maxOperations(self, s: str) -> int:
        arr=[]
        cnt=0
        for c in s:
            if c=='0':
                if cnt:
                    arr.append(cnt)
                    cnt=0
            else:
                cnt+=1
        for i in range(1, len(arr)):
            arr[i]+=arr[i-1]
        return sum(arr)
