class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, c = 0;
        for (int i = 0; i < s.length(); i ++) {
            if (s[i] == '1') {
                if (i >= 1 and s[i-1] == '0') ans += c;
                c ++;
            }
        }        
        if (s.back() == '0') ans += c;
        return ans;
    }
};
