class Solution {
public:
    int maxOperations(string s) {
        long long res = 0, cnt = 0;
        s.push_back('1');
        for(int i = s.size() - 2; i >= 0; i -- ){
            if(s[i] == '1') res += cnt;
            else{
                if(i + 1 < s.size() && s[i + 1] == '1') cnt ++;
            }
        }
        return res;
    }
};
