class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        int idx = -1;
        for(int i = n - 1; i >= 0; i--){
            if(s.charAt(i) == '0'){
                idx = i;
                break;
            }
        }
        if(idx == -1){
            return 0;
        }
        int cc = 0;
        ArrayList<Integer> count = new ArrayList<>();
        for(int i = 0; i < n - 1; i++){
             if(s.charAt(i) == '1'){
                cc++;
            }
            if(s.charAt(i) == '1' && s.charAt(i + 1) == '0'){
                count.add(cc);
            }

        }
        System.out.println(count);
        int ans = 0;
        for(int i = 0; i < count.size(); i++){
            int l = count.get(i);
            ans += l;
        }
        return ans;
    }
}
