class Solution {
public:
    int maxOperations(string s) {
        // 从左开始, 能移动的就移动, 充分利用空格?
        int res = 0, n = s.size();
        bool tag = 0;
        for (int i = 0, cnt = 0; i < n; ++i) {
            if (s[i] == '1') {
                ++cnt;
                tag = 1;
            } else {
                if (tag)
                    res += cnt;
                tag = 0;
            }
        }
        return res;
    }
};
