class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        n = len(s)

        acc = 0
        for i in range(n - 1, -1, -1):
            if s[i] == '0' and (i == n - 1 or s[i + 1] == '1'):
                acc += 1
            elif s[i] == '1':
                ans += acc

            #print(i, ans, acc)

        return ans
