class Solution:
    def maxOperations(self, s: str) -> int:
        count = 0
        num_of_ones = 0
        n = len(s)
        l, r = 0, 0
        while r < n and l < n:
            while l < n and s[l] == '0':
                l += 1
            r = l

            while r < n and s[r] == '1':
                r += 1
            print(l, r, count, num_of_ones)
            if r < n and s[r] == '0':
                count += (num_of_ones + (r-l))
                num_of_ones += (r-l)
            l = r
        return count
