class Solution:
    def maxOperations(self, s: str) -> int:
        arr = []
        last = ''
        for i in s:
            if i=='1':
                if i==last:arr[-1]+=1
                else:arr.append(1)
            last = i
        ans = 0
        if s[-1]=='0':arr.append(0)
        for i in range(len(arr)-1):
            ans += arr[i]
            arr[i+1]+=arr[i]
        return ans
