class Solution:
    def maxOperations(self, s: str) -> int:
        m = len(s)

        left, right = 0, -1
        while left < m and s[left] != '1':
            left += 1
        cnt = 0
        i = 0
        while i < m:
            if s[i] == '1':
                right = i
            else:
                if right != -1:
                    j = i + 1
                    while j < m and s[j] == '0':
                        j += 1
                    lens = right - left + 1
                    cnt += lens
                    left = j - lens
                    right = j - 1
                    i = j - 1
            i += 1
        return cnt
